package Lab2;abstract class Figure {    private double length;    private double width;    private double height;    abstract double calculateArea();    abstract double calculateVolume();    public double getLength() {        return length;    }    public void setLength(double length) {        this.length = length;    }    public double getWidth() {        return width;    }    public void setWidth(double width) {        this.width = width;    }    public double getHeight() {        return height;    }    public void setHeight(double height) {        this.height = height;    }}