package test;public class DemoStack extends FixedStack {    DemoStack(int size, String s) {        super(size, s);        tos = -1;    }    public void push(int item) {        if (tos == stck.length - 1) {            System.out.println("Стек заповнений");        } else {            stck[++tos] = item * 2;        }    }    public void defoult() {        if (tos == stck.length - 1) {            System.out.println("Стек заповнений");        } else {            stck[++tos] = 2;        }    }    public int pop() {        if (tos < 0) {            System.out.println("Стек пустий");            return 0;        } else {            return stck[tos--];        }    }}